import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Auth.css";

export default function Login() {
  const nav = useNavigate();

  const [form, setForm] = useState({
    email: "",
    parola: "",
    rol: "cursant"
  });

  function updateField(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleLogin(e) {
    e.preventDefault();

    const res = await fetch("http://localhost:8000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });

    const data = await res.json();

    if (res.ok) {
      alert("Autentificare reușită!");

      // poți redirecționa diferit în funcție de rol
      if (form.rol === "admin") nav("/admin-dashboard");
      else nav("/");
      
    } else {
      alert(data.detail || "Credențiale invalide");
    }
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Autentificare</h2>

        <form onSubmit={handleLogin}>

          <input name="email" type="email" placeholder="Email"
            value={form.email} onChange={updateField} required />

          <input name="parola" type="password" placeholder="Parolă"
            value={form.parola} onChange={updateField} required />

          <select name="rol" value={form.rol} onChange={updateField}>
            <option value="cursant">Cursant</option>
            <option value="antrenor">Antrenor personal</option>
            <option value="admin">Administrator</option>
          </select>

          <button className="auth-btn">Login</button>
        </form>
      </div>
    </div>
  );
}
