import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Auth.css"; // pentru styling modern

export default function Register() {
  const nav = useNavigate();

  const [form, setForm] = useState({
    nume: "",
    email: "",
    telefon: "",
    cnp: "",
    parola: "",
    rol: "cursant"
  });

  function updateField(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    const res = await fetch("http://localhost:8000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });

    const data = await res.json();

    if (res.ok) {
      alert("Cont creat cu succes!");
      nav("/login");
    } else {
      alert(data.detail || "Eroare la înregistrare");
    }
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Înregistrare</h2>

        <form onSubmit={handleSubmit}>

          <input name="nume" placeholder="Nume complet"
            value={form.nume} onChange={updateField} required />

          <input name="email" type="email" placeholder="Email"
            value={form.email} onChange={updateField} required />

          <input name="telefon" placeholder="Număr de telefon"
            value={form.telefon} onChange={updateField} required />

          <input name="cnp" placeholder="CNP"
            value={form.cnp} onChange={updateField} required />

          <input name="parola" type="password" placeholder="Parolă"
            value={form.parola} onChange={updateField} required />

          <select name="rol" value={form.rol} onChange={updateField}>
            <option value="cursant">Cursant</option>
            <option value="antrenor">Antrenor personal</option>
            <option value="admin">Administrator</option>
          </select>

          <button className="auth-btn">Creează cont</button>
        </form>
      </div>
    </div>
  );
}
